 // If the clause is not found, return null
 return null;
 }

// Extract the clause by finding the first semicolon after the clause
 index = sql.indexOf(";", index);
 if (index == -1) {
 // If no semicolon is found, return the rest of the query
 return sql.substring(clause.length());
 } else {
 // If a semicolon is found, return the clause up to the semicolon
 return sql.substring(clause.length(), index);
 }
 }

/\*\*
